// Dobson's 1-5, Excercise #6// Make Gen2 table for Csound Score#include <stdio.h>#include <math.h>int main (int argc, char* argv[]){int notes, midinotes,i,size,sizeto2;double c5,c0,ratio,frequency;double intervals[24];//when user puts wrong command line...if (argc != 4) 	{	printf("Usage: gen2 notes midinote size\n");	return 1;	}//assigning input and making debugger for wrong valuenotes = atoi(argv[1]); //convert 1st command line (string) to an integerif (notes<1)	{	printf("Error: notes must be positive number!\n");	return 1;	}if (notes>24)	{	printf("Error: maximum value for notes is 24\n");	return 1;	}midinotes = atoi(argv[2]);if (midinotes<1)	{	printf("Error: MIDI note must be positive number!\n");	return 1;	}if (midinotes>127)	{	printf("Error: maximum value for MIDI note is 127\n");	return 1;	}size = atoi(argv[3]);sizeto2 = pow(size,2);// do the calculation of power of 2//calculation of midi to frequency//code from previous chapterratio = pow(2, 1.0/12.0); // find Middle C, three semitones above low A= 220c5 = 220.0*pow(ratio,3);// MIDI note 0 is C, 5 octaves below middle Cc0 = c5 *pow(0.5,5);	frequency = c0*pow(ratio,midinotes);//Calculate and Print list of notesratio = pow(2, 1.0/notes); for (i=0;i<notes;i++) 	{	intervals [i] = frequency;	frequency *= ratio; //multiply frequency with ratio (it's not a pointer...)	}printf("f1 0 %d -2 %d ",sizeto2,i);//write 2 intead of 2 for normalizationfor (i=0;i<notes;i++)	{	printf("%f ",intervals[i]); 	}printf ("\n");return 0;}