// Dobson's 1-5 <nscale.c>// Displays E.T frequencies for an N-note octave, from a given MIDI note#include <stdio.h>#include <math.h>int main (int argc, char* argv[]){int notes, midinotes,i;double c5,c0,ratio,frequency;double intervals[24];//when user puts wrong command line...if (argc != 3) 	{	printf("Usage: nscale notes midinote\n");	return 1;	}//assigning input and making debugger for wrong valuenotes = atoi(argv[1]); //convert 1st command line (string) to an integerif (notes<1)	{	printf("Error: notes must be positive number!\n");	return 1;	}if (notes>24)	{	printf("Error: maximum value for notes is 24\n");	return 1;	}midinotes = atoi(argv[2]);		if (midinotes<1)	{	printf("Error: MIDI note must be positive number!\n");	return 1;	}if (midinotes>127)	{	printf("Error: maximum value for MIDI note is 127\n");	return 1;	}//calculation of midi to frequency//code from previous chapterratio = pow(2, 1.0/12.0); // find Middle C, three semitones above low A= 220c5 = 220.0*pow(ratio,3);// MIDI note 0 is C, 5 octaves below middle Cc0 = c5 *pow(0.5,5);	frequency = c0*pow(ratio,midinotes);//Calculate and Print list of notesratio = pow(2, 1.0/notes); for (i=0;i<(notes+1);i++) //excercise #3	{	intervals [i] = frequency;	frequency *= ratio; //multiply frequency with ratio (it's not a pointer...)	//printf("%f\n",intervals[i]); // this could work, too	}for (i=0;i<(notes+1);i++)	{	printf("%d :      %f\n",i+1,intervals[i]); //excercise #4	}	return 0;}